import React, { useState } from 'react';
import { useForm } from '../../context/FormContext';

const ComprehensionQuestion = ({ question }) => {
  const { updateQuestion } = useForm();
  const [passage, setPassage] = useState(question.comprehensionData?.passage || '');
  const [newQuestion, setNewQuestion] = useState({ text: '', options: [], correctAnswer: '' });
  const [newOption, setNewOption] = useState('');

  const handlePassageChange = (e) => {
    const newPassage = e.target.value;
    setPassage(newPassage);
    updateQuestion(question.id, {
      comprehensionData: {
        ...question.comprehensionData,
        passage: newPassage
      }
    });
  };

  const handleAddOption = () => {
    if (!newOption.trim()) return;
    const updatedOptions = [...newQuestion.options, newOption.trim()];
    setNewQuestion({ ...newQuestion, options: updatedOptions });
    setNewOption('');
  };

  const handleAddQuestion = () => {
    if (!newQuestion.text.trim() || !newQuestion.correctAnswer) return;
    const subQuestion = {
      id: Date.now().toString(),
      questionText: newQuestion.text,
      options: newQuestion.options,
      correctAnswer: newQuestion.correctAnswer
    };
    const updatedQuestions = [...(question.comprehensionData?.questions || []), subQuestion];
    updateQuestion(question.id, {
      comprehensionData: {
        ...question.comprehensionData,
        questions: updatedQuestions
      }
    });
    setNewQuestion({ text: '', options: [], correctAnswer: '' });
  };

  const handleDeleteSubQuestion = (questionId) => {
    const updatedQuestions = question.comprehensionData.questions.filter(q => q.id !== questionId);
    updateQuestion(question.id, {
      comprehensionData: {
        ...question.comprehensionData,
        questions: updatedQuestions
      }
    });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200 mb-6">
      <div className="flex justify-between items-start mb-4">
        <input
          type="text"
          value={question.questionText}
          onChange={(e) => updateQuestion(question.id, { questionText: e.target.value })}
          placeholder="Question"
          className="text-xl font-semibold border-none focus:ring-0 px-0 py-1 w-full"
        />
      </div>

      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-2">Passage:</label>
        <textarea
          value={passage}
          onChange={handlePassageChange}
          placeholder="Enter the comprehension passage"
          className="w-full border border-gray-300 rounded px-3 py-2"
          rows="4"
        />
      </div>

      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-2">Add Sub-Question:</label>
        <input
          type="text"
          value={newQuestion.text}
          onChange={(e) => setNewQuestion({ ...newQuestion, text: e.target.value })}
          placeholder="Question text"
          className="w-full border border-gray-300 rounded px-3 py-2 mb-2"
        />

        <div className="flex items-center mb-2">
          <input
            type="text"
            value={newOption}
            onChange={(e) => setNewOption(e.target.value)}
            placeholder="Option text"
            className="border border-gray-300 rounded px-3 py-1 text-sm w-48 mr-2"
          />
          <button
            onClick={handleAddOption}
            className="bg-blue-100 text-blue-600 px-3 py-1 rounded text-sm"
          >
            Add Option
          </button>
        </div>

        <div className="mb-2">
          {newQuestion.options.length > 0 && (
            <>
              <label className="block text-sm font-medium text-gray-700 mb-1">Options:</label>
              <div className="flex flex-wrap gap-2 mb-2">
                {newQuestion.options.map((option, idx) => (
                  <div key={idx} className="bg-gray-100 px-3 py-1 rounded-full text-sm">
                    {option}
                  </div>
                ))}
              </div>
              <select
                value={newQuestion.correctAnswer}
                onChange={(e) => setNewQuestion({ ...newQuestion, correctAnswer: e.target.value })}
                className="border border-gray-300 rounded px-2 py-1 text-sm"
              >
                <option value="">Select correct answer</option>
                {newQuestion.options.map((option, idx) => (
                  <option key={idx} value={option}>{option}</option>
                ))}
              </select>
            </>
          )}
        </div>

        <button
          onClick={handleAddQuestion}
          disabled={!newQuestion.text || !newQuestion.correctAnswer}
          className="bg-green-100 text-green-600 px-3 py-1 rounded text-sm disabled:opacity-50"
        >
          Add Sub-Question
        </button>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Sub-Questions:</label>
        {question.comprehensionData?.questions?.map((q, idx) => (
          <div key={q.id} className="mb-3 p-3 bg-gray-50 rounded">
            <div className="flex justify-between items-center mb-1">
              <span className="font-medium">{idx + 1}. {q.questionText}</span>
              <button
                onClick={() => handleDeleteSubQuestion(q.id)}
                className="text-red-500"
              >
                ×
              </button>
            </div>
            <div className="text-sm text-gray-600">
              Options: {q.options.join(', ')}
              <br />
              Correct: {q.correctAnswer}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ComprehensionQuestion;
