import React, { useState } from 'react';
import { useForm } from '../../context/FormContext';

const ClozeQuestion = ({ question }) => {
  const { updateQuestion } = useForm();
  const [text, setText] = useState(question.clozeData?.text || '');
  const [blankText, setBlankText] = useState('');

  const handleTextChange = (e) => {
    const newText = e.target.value;
    setText(newText);
    updateQuestion(question.id, { 
      clozeData: {
        ...question.clozeData,
        text: newText
      }
    });
  };

  const handleAddBlank = () => {
    if (!blankText.trim()) return;
    const newBlank = {
      id: Date.now().toString(),
      correctAnswer: blankText.trim()
    };
    const updatedBlanks = [...(question.clozeData?.blanks || []), newBlank];
    updateQuestion(question.id, {
      clozeData: {
        ...question.clozeData,
        blanks: updatedBlanks
      }
    });
    setBlankText('');
  };

  const handleDeleteBlank = (blankId) => {
    const updatedBlanks = question.clozeData.blanks.filter(b => b.id !== blankId);
    updateQuestion(question.id, {
      clozeData: {
        ...question.clozeData,
        blanks: updatedBlanks
      }
    });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200 mb-6">
      <div className="flex justify-between items-start mb-4">
        <input
          type="text"
          value={question.questionText}
          onChange={(e) => updateQuestion(question.id, { questionText: e.target.value })}
          placeholder="Question"
          className="text-xl font-semibold border-none focus:ring-0 px-0 py-1 w-full"
        />
      </div>

      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-2">Passage Text:</label>
        <textarea
          value={text}
          onChange={handleTextChange}
          placeholder="Enter the passage text with blanks (e.g., The ___ jumped over the fence)"
          className="w-full border border-gray-300 rounded px-3 py-2"
          rows="3"
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Blanks:</label>
        <div className="flex items-center mb-2">
          <input
            type="text"
            value={blankText}
            onChange={(e) => setBlankText(e.target.value)}
            placeholder="Blank answer"
            className="border border-gray-300 rounded px-3 py-1 text-sm w-48 mr-2"
          />
          <button
            onClick={handleAddBlank}
            className="bg-blue-100 text-blue-600 px-3 py-1 rounded text-sm"
          >
            Add Blank
          </button>
        </div>
        <div className="space-y-2">
          {question.clozeData?.blanks?.map(blank => (
            <div key={blank.id} className="flex items-center">
              <span className="mr-2">Blank: ____</span>
              <span className="text-sm text-gray-500 mr-2">→</span>
              <span className="text-sm text-blue-600 mr-2">{blank.correctAnswer}</span>
              <button
                onClick={() => handleDeleteBlank(blank.id)}
                className="text-red-500"
              >
                ×
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ClozeQuestion;
