import React, { useState } from 'react';
import { useForm } from '../../context/FormContext';

const CategorizeQuestion = ({ question }) => {
  const { updateQuestion } = useForm();
  const [newCategory, setNewCategory] = useState('');
  const [newItem, setNewItem] = useState({ text: '', category: '' });

  const handleAddCategory = () => {
    if (!newCategory.trim()) return;
    const updatedCategories = [
      ...question.categories,
      {
        id: Date.now().toString(),
        name: newCategory.trim(),
        options: []
      }
    ];
    updateQuestion(question.id, { categories: updatedCategories });
    setNewCategory('');
  };

  const handleAddItem = () => {
    if (!newItem.text.trim() || !newItem.category) return;
    const updatedItems = [
      ...question.items,
      {
        id: Date.now().toString(),
        text: newItem.text.trim(),
        belongsTo: newItem.category
      }
    ];
    updateQuestion(question.id, { items: updatedItems });
    setNewItem({ text: '', category: '' });
  };

  const handleDeleteCategory = (categoryId) => {
    const updatedCategories = question.categories.filter(c => c.id !== categoryId);
    const updatedItems = question.items.filter(i => i.belongsTo !== categoryId);
    updateQuestion(question.id, { 
      categories: updatedCategories,
      items: updatedItems
    });
  };

  const handleDeleteItem = (itemId) => {
    const updatedItems = question.items.filter(i => i.id !== itemId);
    updateQuestion(question.id, { items: updatedItems });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200 mb-6">
      <div className="flex justify-between items-start mb-4">
        <input
          type="text"
          value={question.questionText}
          onChange={(e) => updateQuestion(question.id, { questionText: e.target.value })}
          placeholder="Question"
          className="text-xl font-semibold border-none focus:ring-0 px-0 py-1 w-full"
        />
      </div>

      <div className="mb-4">
        <div className="flex items-center mb-2">
          <span className="text-sm font-medium text-gray-700 mr-2">Categories:</span>
          <input
            type="text"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
            placeholder="New category name"
            className="border border-gray-300 rounded px-3 py-1 text-sm w-48 mr-2"
          />
          <button
            onClick={handleAddCategory}
            className="bg-blue-100 text-blue-600 px-3 py-1 rounded text-sm"
          >
            Add
          </button>
        </div>
        <div className="flex flex-wrap gap-2 mb-4">
          {question.categories.map(category => (
            <div key={category.id} className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm flex items-center">
              {category.name}
              <button
                onClick={() => handleDeleteCategory(category.id)}
                className="ml-1 text-blue-500 hover:text-blue-700"
              >
                ×
              </button>
            </div>
          ))}
        </div>
      </div>

      <div>
        <div className="flex items-center mb-2">
          <span className="text-sm font-medium text-gray-700 mr-2">Items:</span>
          <input
            type="text"
            value={newItem.text}
            onChange={(e) => setNewItem({ ...newItem, text: e.target.value })}
            placeholder="Item text"
            className="border border-gray-300 rounded px-3 py-1 text-sm w-48 mr-2"
          />
          <select
            value={newItem.category}
            onChange={(e) => setNewItem({ ...newItem, category: e.target.value })}
            className="border border-gray-300 rounded px-2 py-1 text-sm mr-2"
          >
            <option value="">Select category</option>
            {question.categories.map(category => (
              <option key={category.id} value={category.id}>{category.name}</option>
            ))}
          </select>
          <button
            onClick={handleAddItem}
            className="bg-blue-100 text-blue-600 px-3 py-1 rounded text-sm"
          >
            Add
          </button>
        </div>
        <div className="space-y-2">
          {question.items.map(item => (
            <div key={item.id} className="flex items-center">
              <span className="mr-2">{item.text}</span>
              <span className="text-sm text-gray-500 mr-2">→</span>
              <span className="text-sm text-blue-600 mr-2">
                {question.categories.find(c => c.id === item.belongsTo)?.name || 'Uncategorized'}
              </span>
              <button
                onClick={() => handleDeleteItem(item.id)}
                className="text-red-500"
              >
                ×
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CategorizeQuestion;
