import React from 'react';
import { useForm } from '../context/FormContext';
import FormHeader from '../components/FormHeader';
import QuestionList from '../components/QuestionList';
import Sidebar from '../components/Sidebar';
import PreviewModal from '../components/PreviewModal';

const FormBuilder = () => {
  const { form, saveForm } = useForm();
  const [showPreview, setShowPreview] = React.useState(false);
  const [activeTab, setActiveTab] = React.useState('questions');

  return (
    <div className="flex h-screen bg-gray-50">
      <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} />

      <div className="flex-1 overflow-auto">
        {/* Header */}
        <div className="bg-indigo-600 text-white p-4 shadow-md">
          <h1 className="text-2xl font-bold">Form Builder</h1>
        </div>

        {/* Main Content */}
        <div className="p-6">
          {activeTab === 'questions' && (
            <>
              <FormHeader 
                title={form.title}
                description={form.description}
                headerImage={form.headerImage}
              />

              <QuestionList questions={form.questions} />

              <div className="max-w-3xl mx-auto mt-6 flex justify-end space-x-4">
                <button
                  onClick={() => setShowPreview(true)}
                  className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-lg shadow transition-colors"
                >
                  <i className="fas fa-eye mr-2"></i> Preview
                </button>
                <button
                  onClick={saveForm}
                  className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded-lg shadow transition-colors"
                >
                  <i className="fas fa-save mr-2"></i> Save Form
                </button>
              </div>
            </>
          )}

          {activeTab === 'settings' && (
            <div className="max-w-3xl mx-auto bg-white p-6 rounded-lg shadow">
              <h2 className="text-xl font-semibold mb-4 text-gray-800">Form Settings</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Form Theme Color</label>
                  <div className="flex space-x-2">
                    {['indigo', 'blue', 'green', 'purple', 'pink'].map(color => (
                      <button
                        key={color}
                        className={`w-8 h-8 rounded-full bg-${color}-500 hover:bg-${color}-600 border-2 border-white shadow`}
                      ></button>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {showPreview && (
        <PreviewModal 
          form={form}
          onClose={() => setShowPreview(false)}
        />
      )}
    </div>
  );
};

export default FormBuilder;
