import React from 'react';
import { useForm } from '../context/FormContext';

const Sidebar = () => {
  const { addQuestion } = useForm();

  const handleAddQuestion = (type) => {
    addQuestion(type);
  };

  return (
    <div className="w-64 bg-white border-r border-gray-200 p-4">
      <h1 className="text-xl font-bold mb-6">Form Builder</h1>

      <div className="mb-6">
        <h2 className="text-sm font-semibold text-gray-500 uppercase mb-2">Question Types</h2>
        <div className="space-y-2">
          <button 
            onClick={() => handleAddQuestion('categorize')}
            className="w-full text-left px-3 py-2 bg-gray-100 hover:bg-gray-200 rounded-md"
          >
            <span className="mr-2">üóÇ</span> Categorize
          </button>
          <button 
            onClick={() => handleAddQuestion('cloze')}
            className="w-full text-left px-3 py-2 bg-gray-100 hover:bg-gray-200 rounded-md"
          >
            <span className="mr-2">‚úçÔ∏è</span> Cloze
          </button>
          <button 
            onClick={() => handleAddQuestion('comprehension')}
            className="w-full text-left px-3 py-2 bg-gray-100 hover:bg-gray-200 rounded-md"
          >
            <span className="mr-2">üìñ</span> Comprehension
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
