import React, { useState, useRef } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

// This is the main App component for the form builder UI.
const App = () => {
  // State to hold the list of questions in the form.
  const [questions, setQuestions] = useState([]);
  // State to hold the form's header image URL.
  const [headerImage, setHeaderImage] = useState(null);
  // State to hold the form's title and description.
  const [formDetails, setFormDetails] = useState({
    title: 'Your Form Title',
    description: 'This is a description for your form. Fill out the details below.',
  });

  // A list of available question types to be dragged into the form.
  const formElements = [
    { id: '1', type: 'Categorize', icon: 'fas fa-sort-amount-down', title: 'Categorize' },
    { id: '2', type: 'Cloze', icon: 'fas fa-fill-drip', title: 'Cloze' },
    { id: '3', type: 'Comprehension', icon: 'fas fa-book-reader', title: 'Comprehension' },
    { id: '4', type: 'Text Input', icon: 'fas fa-font', title: 'Text Input' },
  ];

  // Function to handle the result of a drag-and-drop operation.
  const onDragEnd = (result) => {
    const { source, destination } = result;

    // If the item was dropped outside of a droppable area, do nothing.
    if (!destination) {
      return;
    }

    // If the item was dropped within the form canvas.
    if (destination.droppableId === 'form-canvas') {
      // Find the question type that was dragged.
      const draggedElement = formElements.find(el => el.id === result.draggableId);
      if (draggedElement) {
        // Create a new question object with a unique ID and default properties.
        const newQuestion = {
          id: `question-${new Date().getTime()}`,
          type: draggedElement.type,
          title: `New ${draggedElement.type} Question`,
          content: {}, // Will hold type-specific content.
          image: null, // Holds the image URL for the question.
        };
        // Add the new question to the list at the drop position.
        const newQuestions = Array.from(questions);
        newQuestions.splice(destination.index, 0, newQuestion);
        setQuestions(newQuestions);
      }
    } else if (source.droppableId === 'form-canvas' && destination.droppableId === 'form-canvas') {
      // Reorder questions within the form canvas.
      const newQuestions = Array.from(questions);
      const [reorderedItem] = newQuestions.splice(source.index, 1);
      newQuestions.splice(destination.index, 0, reorderedItem);
      setQuestions(newQuestions);
    }
  };

  // Function to update a question's details (e.g., its title).
  const updateQuestion = (id, newProps) => {
    setQuestions(questions.map(q => q.id === id ? { ...q, ...newProps } : q));
  };

  // Function to delete a question from the form.
  const deleteQuestion = (id) => {
    setQuestions(questions.filter(q => q.id !== id));
  };

  // Function to handle the header image upload.
  const handleHeaderImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Use FileReader to create a URL for the image preview.
      const reader = new FileReader();
      reader.onloadend = () => {
        setHeaderImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Render a question component based on its type.
  const renderQuestion = (question, index) => {
    switch (question.type) {
      case 'Text Input':
        return <TextInputQuestion question={question} index={index} updateQuestion={updateQuestion} deleteQuestion={deleteQuestion} />;
      case 'Categorize':
        return <CategorizeQuestion question={question} index={index} updateQuestion={updateQuestion} deleteQuestion={deleteQuestion} />;
      case 'Cloze':
        return <ClozeQuestion question={question} index={index} updateQuestion={updateQuestion} deleteQuestion={deleteQuestion} />;
      case 'Comprehension':
        return <ComprehensionQuestion question={question} index={index} updateQuestion={updateQuestion} deleteQuestion={deleteQuestion} />;
      default:
        return null;
    }
  };

  return (
    // Wrap the entire app in DragDropContext to enable drag-and-drop.
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="flex flex-col min-h-screen bg-gray-50 text-gray-800 font-inter">

        {/* Header */}
        <header className="bg-white shadow-sm sticky top-0 z-50">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <i className="fas fa-edit text-indigo-600 text-2xl"></i>
              <h1 className="text-2xl font-bold text-indigo-800 tracking-wide">Form Builder</h1>
            </div>
            <div className="flex items-center space-x-4">
              <button className="bg-indigo-600 hover:bg-indigo-700 text-white font-medium py-2 px-4 rounded-full transition-colors duration-200 shadow-md">
                Save Form
              </button>
              <button className="bg-white text-indigo-600 border border-indigo-600 font-medium py-2 px-4 rounded-full transition-colors duration-200 hover:bg-indigo-50">
                Preview
              </button>
            </div>
          </div>
        </header>

        {/* Main Content Area */}
        <main className="flex-1 overflow-y-auto p-4 sm:p-6 lg:p-8">
          <div className="max-w-7xl mx-auto h-full flex flex-col lg:flex-row space-y-6 lg:space-y-0 lg:space-x-6">

            {/* Left Sidebar - Form Elements (The source for drag-and-drop) */}
            <aside className="w-full lg:w-1/4 flex-shrink-0 bg-white rounded-xl shadow-lg p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-6">Form Elements</h2>
              <Droppable droppableId="form-elements" isDropDisabled={true}>
                {(provided) => (
                  <div {...provided.droppableProps} ref={provided.innerRef} className="space-y-4">
                    {formElements.map((el, index) => (
                      <Draggable key={el.id} draggableId={el.id} index={index}>
                        {(provided, snapshot) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            className={`
                              bg-gray-100 p-4 rounded-lg flex items-center space-x-4 cursor-grab transition-colors duration-200 group
                              ${snapshot.isDragging ? 'bg-indigo-200 shadow-xl' : 'hover:bg-indigo-50'}
                            `}
                          >
                            <i className="fas fa-grip-lines text-gray-500 group-hover:text-indigo-600"></i>
                            <i className={`${el.icon} text-indigo-600`}></i>
                            <span className="font-medium text-gray-700 group-hover:text-indigo-800">{el.title}</span>
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </aside>

            {/* Main Form Canvas (The destination for drag-and-drop) */}
            <section className="flex-1 bg-white rounded-xl shadow-lg p-6 relative">
              {/* Header Image section */}
              <div className="mb-8 p-4 bg-gray-50 rounded-lg border-2 border-dashed border-gray-200 flex flex-col items-center justify-center relative group">
                {headerImage && (
                  <img src={headerImage} alt="Form Header" className="w-full h-48 object-cover rounded-lg mb-4" />
                )}
                <label className="cursor-pointer text-indigo-600 hover:text-indigo-800 transition-colors duration-200 font-medium">
                  <i className="fas fa-image mr-2"></i>
                  {headerImage ? 'Change Header Image' : 'Add Header Image'}
                  <input type="file" className="hidden" onChange={handleHeaderImageUpload} accept="image/*" />
                </label>
                {headerImage && (
                  <button onClick={() => setHeaderImage(null)} className="absolute top-2 right-2 text-red-500 hover:text-red-700">
                    <i className="fas fa-times-circle text-xl"></i>
                  </button>
                )}
              </div>

              {/* Form Title & Description Section */}
              <div className="bg-white p-8 rounded-lg mb-8 shadow-sm">
                <input
                  type="text"
                  value={formDetails.title}
                  onChange={(e) => setFormDetails({ ...formDetails, title: e.target.value })}
                  className="w-full text-3xl font-bold text-gray-900 border-0 border-b-2 border-gray-300 focus:ring-0 focus:border-indigo-500 mb-2 p-0"
                  placeholder="Your Form Title"
                />
                <textarea
                  value={formDetails.description}
                  onChange={(e) => setFormDetails({ ...formDetails, description: e.target.value })}
                  className="w-full text-gray-500 border-0 focus:ring-0 focus:border-indigo-500 p-0 resize-none"
                  placeholder="This is a description for your form."
                />
              </div>

              {/* Questions Drop Area */}
              <Droppable droppableId="form-canvas">
                {(provided) => (
                  <div {...provided.droppableProps} ref={provided.innerRef} className="space-y-6 min-h-64">
                    {questions.length > 0 ? (
                      questions.map((question, index) => (
                        <Draggable key={question.id} draggableId={question.id} index={index}>
                          {(provided, snapshot) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              className={`
                                bg-white p-6 rounded-lg shadow-md border border-gray-200 transition-all duration-200
                                ${snapshot.isDragging ? 'rotate-1 scale-105 shadow-2xl' : ''}
                              `}
                            >
                              <div className="flex items-center justify-between mb-4">
                                <div className="flex items-center space-x-3">
                                  <div {...provided.dragHandleProps}>
                                    <i className="fas fa-grip-vertical text-gray-400 cursor-grab hover:text-indigo-600 transition-colors"></i>
                                  </div>
                                  <span className="bg-indigo-100 text-indigo-600 text-xs font-semibold px-2.5 py-0.5 rounded-full">{question.type}</span>
                                </div>
                                <button onClick={() => deleteQuestion(question.id)} className="text-red-500 hover:text-red-700 transition-colors">
                                  <i className="fas fa-trash-alt"></i>
                                </button>
                              </div>
                              {renderQuestion(question, index)}
                            </div>
                          )}
                        </Draggable>
                      ))
                    ) : (
                      <div className="absolute inset-0 flex items-center justify-center pointer-events-none opacity-40">
                        <span className="text-gray-400 text-3xl font-bold">Drag & Drop elements here</span>
                      </div>
                    )}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </section>
          </div>
        </main>
      </div>
    </DragDropContext>
  );
};

// --- Question Components ---

// Generic component to handle an image upload for a question.
const QuestionImageUpload = ({ onImageUpload, imageUrl, onDeleteImage }) => {
  return (
    <div className="mt-4 p-4 bg-gray-50 rounded-lg border-2 border-dashed border-gray-200 flex flex-col items-center justify-center relative group">
      {imageUrl && (
        <img src={imageUrl} alt="Question" className="w-full max-h-48 object-cover rounded-lg mb-4" />
      )}
      <label className="cursor-pointer text-indigo-600 hover:text-indigo-800 transition-colors duration-200 font-medium">
        <i className="fas fa-image mr-2"></i>
        {imageUrl ? 'Change Image' : 'Add Image'}
        <input type="file" className="hidden" onChange={onImageUpload} accept="image/*" />
      </label>
      {imageUrl && (
        <button onClick={onDeleteImage} className="absolute top-2 right-2 text-red-500 hover:text-red-700">
          <i className="fas fa-times-circle text-xl"></i>
        </button>
      )}
    </div>
  );
};

// Component for a simple Text Input question.
const TextInputQuestion = ({ question, updateQuestion }) => {
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        updateQuestion(question.id, { image: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const deleteImage = () => {
    updateQuestion(question.id, { image: null });
  };

  return (
    <div>
      <input
        type="text"
        value={question.title}
        onChange={(e) => updateQuestion(question.id, { title: e.target.value })}
        className="w-full text-lg font-medium text-gray-900 border-0 border-b-2 border-gray-200 focus:ring-0 focus:border-indigo-500 p-0 mb-4"
        placeholder="Enter question title..."
      />
      <input
        type="text"
        disabled
        className="w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 transition-colors duration-200"
        placeholder="User's answer will appear here..."
      />
      <QuestionImageUpload onImageUpload={handleImageUpload} imageUrl={question.image} onDeleteImage={deleteImage} />
    </div>
  );
};

