import React from 'react';

const PreviewModal = ({ form, onClose }) => {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg w-full max-w-2xl max-h-[90vh] overflow-auto">
        <div className="p-4 border-b border-gray-200 flex justify-between items-center">
          <h3 className="text-lg font-semibold">Form Preview</h3>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        <div className="p-6">
          <div className="mb-8">
            {form.headerImage && (
              <img 
                src={`http://localhost:5000/uploads/${form.headerImage}`} 
                alt="Header" 
                className="w-full h-48 object-cover rounded-lg mb-4"
              />
            )}
            <h1 className="text-3xl font-bold mb-2">{form.title || 'Untitled Form'}</h1>
            {form.description && <p className="text-gray-600 mb-4">{form.description}</p>}
          </div>

          {form.questions.map((question, index) => (
            <div key={question.id} className="mb-6">
              <h3 className="text-xl font-semibold mb-2">{index + 1}. {question.questionText}</h3>
              {question.questionImage && (
                <img 
                  src={`http://localhost:5000/uploads/${question.questionImage}`} 
                  alt="Question" 
                  className="w-full max-h-64 object-contain mb-4"
                />
              )}
              {/* Render question preview based on type */}
              {question.type === 'categorize' && (
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-gray-500 mb-2">Categorize question preview</p>
                </div>
              )}
              {question.type === 'cloze' && (
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-gray-500 mb-2">Cloze question preview</p>
                </div>
              )}
              {question.type === 'comprehension' && (
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-gray-500 mb-2">Comprehension question preview</p>
                </div>
              )}
            </div>
          ))}
        </div>
        <div className="p-4 border-t border-gray-200 text-right">
          <button className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700">
            Submit
          </button>
        </div>
      </div>
    </div>
  );
};

export default PreviewModal;
